export function displayAlert(msg,type,time){    
    let alert=document.querySelector(".alert-msg");
    alert.textContent=msg;
    alert.classList.add(`alert-${type}`);
    
    setTimeout(function () {
        alert.textContent="' ";
        alert.classList.remove(`alert-${type}`);
    }, time);
}


import {addCityOption} from './init.js'
export async function  populateForm(data){
            
    for (const [key, value] of Object.entries(data)) {
        let ele=document.getElementsByName(`${key}`);        
        ele[0].value=value;
    }
     

    await addCityOption();
    let ele=document.getElementsByName('city');
    ele[0].value=data.city;
 


}

